const console_log = console.log;

/**
 * Filter log from the library
 * @param {string[]|string} filters The name or names of caller to be filtered
 */
function filterLog(filters) {
    if (typeof filters == 'string') filters = [filters];
    console.log = (...params) => {
        let e = new Error(),
            stack = e.stack.split('\n');
        stack.splice(0, 2);
        let caller = stack.shift().trim();
        for (let filter of filters)
            if (caller.indexOf(filter) !== -1) return;
        console_log(...params);
    };
}

module.exports = filterLog;
